function Replace-TextInFile {
    param (
        [string]$FilePath,
        [string]$Search,
        [string]$Replace
    )

    # Escapar caracteres especiales en el patrón de búsqueda
    $EscapedSearch = [regex]::Escape($Search)

    # Reemplazar el texto en el archivo
    (Get-Content $FilePath) -replace $EscapedSearch, $Replace | Out-File -Encoding ASCII $FilePath
}

# Ruta de la carpeta
$rutaCarpeta = "C:\AL"

# Inicializar la variable $Logfile
$Logfile = @()

# Obtener la lista de archivos en la carpeta con la extensión .al
